        THT = (SLL * G1)
        THT = int(THT)
        THHT = str(THT)
        T1 = "1" + ";" + TA1 + ";" + "Cái" + ";" + SLLL + ";" + GG1 + ";" + THHT + "\n"
        j = ";"
        ghi.write(TT)
        ghi.write(TT1)
        ghi.write(T1)
        if (SP > 1):
            G = self.TA_3.text()
            G = int(G) * a
            GG = str(G)
            TA = self.TA_2.text()
            TA = str(TA)
            SLL1 = self.SL_2.text()
            SLL1 = int (SLL1)
            SLLL1 = str (SLL1)
            GL = G * SLL1
            GL = int(GL)
            GGL = str(GL)
            TTT = GL + THT
            TTT = str(TTT)
            TTTT = int(TTT)
            THTT = ";" + "Tổng tiền"
            TTT = THTT + ";;;;" + TTT + "\n"
            VT = TTTT /100 * V1
            VT1 = int(VT)
            VT2 = str(VT1)
            T7 = "2" + ";" + TA + ";" + "Cái" + ";" + SLLL1 + ";" + GG + ";" + GGL +"\n"
            V = ";" + "Phí VAT:" +";;;;"+ VT2 + "\n"
            DC1 = ";"+"Đã cọc ngày:" + TG + ";;;;" + C1 + "\n"
            CL1 = TTTT - C + VT1
